def function(A):
        # UNVECTORIZE THIS
        result = [[0] * len(A[0]) for _ in range(len(A))]
        for i in range(len(A)):
            for j in range(len(A[0])):
                result[len(A)-1-i][len(A[0])-1-j] = A[i][j]
        return result
